document.addEventListener('DOMContentLoaded', function() {
    // Preloader
    window.addEventListener('load', function() {
        const preloader = document.querySelector('.preloader');
        if (preloader) {
            preloader.classList.add('hidden');
            setTimeout(() => {
                preloader.style.display = 'none';
            }, 500);
        }
    });

    // Mobile menu
    const menuToggle = document.querySelector('.menu-toggle');
    const navList = document.querySelector('.nav-list');
    const navLinks = document.querySelectorAll('.nav-link');
    
    if (menuToggle && navList) {
        menuToggle.addEventListener('click', function(e) {
            e.stopPropagation();
            this.classList.toggle('active');
            navList.classList.toggle('active');
            document.body.classList.toggle('no-scroll');
        });
        
        // Close menu when clicking outside
        document.addEventListener('click', function(e) {
            if (navList.classList.contains('active') && 
                !e.target.closest('.nav-list') && 
                !e.target.closest('.menu-toggle')) {
                menuToggle.classList.remove('active');
                navList.classList.remove('active');
                document.body.classList.remove('no-scroll');
            }
        });
        
        // Close menu on link click
        navLinks.forEach(link => {
            link.addEventListener('click', () => {
                menuToggle.classList.remove('active');
                navList.classList.remove('active');
                document.body.classList.remove('no-scroll');
            });
        });
        
        // Prevent scroll when menu is open
        document.addEventListener('scroll', function() {
            if (navList.classList.contains('active')) {
                window.scrollTo(0, 0);
            }
        });
    }

    // Smooth scroll
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function(e) {
            const targetId = this.getAttribute('href');
            if (targetId === '#') return;
            
            e.preventDefault();
            const target = document.querySelector(targetId);
            
            if (target) {
                const headerHeight = document.querySelector('.header')?.offsetHeight || 0;
                const targetPosition = target.getBoundingClientRect().top + window.pageYOffset - headerHeight;
                
                window.scrollTo({
                    top: targetPosition,
                    behavior: 'smooth'
                });
                
                // Update URL
                history.pushState?.({}, '', targetId) || (location.hash = targetId);
            }
        });
    });

    // Current year
    const currentYear = document.getElementById('current-year');
    if (currentYear) {
        currentYear.textContent = new Date().getFullYear();
    }

    // Back to top
    const backToTop = document.querySelector('.back-to-top');
    if (backToTop) {
        window.addEventListener('scroll', function() {
            const isVisible = window.pageYOffset > 300;
            backToTop.style.opacity = isVisible ? '1' : '0';
            backToTop.style.visibility = isVisible ? 'visible' : 'hidden';
        });
    }

    // Certificates toggle
    const toggleBtn = document.getElementById('toggleButton');
    const diploms = document.getElementById('diplom');
    
    if (toggleBtn && diploms) {
        toggleBtn.addEventListener('click', function() {
            const isVisible = diploms.classList.toggle('visible');
            this.querySelector('span').textContent = isVisible ? 'Скрыть сертификаты' : 'Показать сертификаты';
            this.querySelector('i').className = isVisible ? 'fas fa-chevron-up' : 'fas fa-chevron-down';
        });
    }

    // Scroll animations
    const animateOnScroll = function() {
        const elements = document.querySelectorAll('.section, .work-item, .skill-item');
        
        elements.forEach(el => {
            const rect = el.getBoundingClientRect();
            const isVisible = rect.top < window.innerHeight - 100;
            
            if (isVisible) {
                el.style.opacity = '1';
                el.style.transform = 'translateY(0)';
            }
        });
    };
    
    // Initialize elements
    const animatedElements = document.querySelectorAll('.section, .work-item, .skill-item');
    animatedElements.forEach(el => {
        el.style.opacity = '0';
        el.style.transform = 'translateY(30px)';
        el.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
    });
    
    window.addEventListener('scroll', animateOnScroll);
    animateOnScroll();
});