document.addEventListener('DOMContentLoaded', function() {
    // Mobile menu
    const menuToggle = document.querySelector('.menu-toggle');
    const navList = document.querySelector('.nav-list');
    
    if (menuToggle && navList) {
        menuToggle.addEventListener('click', function() {
            navList.classList.toggle('active');
            this.classList.toggle('active');
        });
    }

    // Contact button
    const contactBtn = document.getElementById('contact-btn');
    if (contactBtn) {
        contactBtn.addEventListener('click', function() {
            const contactSection = document.getElementById('contact');
            if (contactSection) {
                contactSection.scrollIntoView({ behavior: 'smooth' });
            }
        });
    }

    // Current year
    const currentYear = document.getElementById('current-year');
    if (currentYear) {
        currentYear.textContent = new Date().getFullYear();
    }

    // Scroll animations
    const animateOnScroll = function() {
        const elements = document.querySelectorAll('[data-animate]');
        const windowHeight = window.innerHeight;
        
        elements.forEach(element => {
            const elementPosition = element.getBoundingClientRect().top;
            
            if (elementPosition < windowHeight - 100) {
                element.classList.add('animated');
            }
        });
    };
    
    // Initialize animations
    const sections = document.querySelectorAll('.section');
    sections.forEach(section => {
        section.setAttribute('data-animate', '');
    });
    
    window.addEventListener('scroll', animateOnScroll);
    animateOnScroll();

    // Animate skill bars
    const skillBars = document.querySelectorAll('.skill-progress');
    if (skillBars.length) {
        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const width = entry.target.parentElement.previousElementSibling.lastElementChild.textContent;
                    entry.target.style.width = width;
                    observer.unobserve(entry.target);
                }
            });
        }, { threshold: 0.5 });
        
        skillBars.forEach(bar => observer.observe(bar));
    }
});
 // Certificates toggle
 document.addEventListener('DOMContentLoaded', function() {
    const toggleCerts = document.getElementById('toggle-certs');
    const certsContainer = document.getElementById('certificates-container');
    const certsGrid = document.querySelector('.certificates-grid');

    if (toggleCerts && certsContainer && certsGrid) {
        toggleCerts.addEventListener('click', function() {
            const isExpanded = this.getAttribute('aria-expanded') === 'true';
            
            // Обновляем атрибуты доступности
            this.setAttribute('aria-expanded', !isExpanded);
            
            // Переключаем классы для анимации
            certsGrid.classList.toggle('active');
            this.classList.toggle('active');
            
            // Обновляем иконку и текст
            const icon = this.querySelector('i');
            const text = this.querySelector('span');
            icon.className = isExpanded ? 'fas fa-chevron-down' : 'fas fa-chevron-up';
            text.textContent = isExpanded ? 'Показать сертификаты' : 'Скрыть сертификаты';
            
            // Управляем видимостью контейнера
            if (isExpanded) {
                certsContainer.hidden = true;
            } else {
                certsContainer.hidden = false;
            }
        });
    }
});
